import React, { useState, useEffect } from 'react';
import { w3cwebsocket as W3CWebSocket } from 'websocket';
import axios from 'axios';

const client = new W3CWebSocket('ws://127.0.0.1:8000/');

function App() {
    const [cacheItems, setCacheItems] = useState({});
    const [key, setKey] = useState('');
    const [value, setValue] = useState('');
    const [expiration, setExpiration] = useState('');

    useEffect(() => {
        client.onopen = () => {
            console.log('WebSocket Client Connected');
        };
        client.onmessage = (message) => {
            const dataFromServer = JSON.parse(message.data);
            setCacheItems(dataFromServer);
        };

        return () => {
            client.close();
        };
    }, []);

    useEffect(() => {
        const interval = setInterval(() => {
            const now = Date.now();
            setCacheItems((prevItems) => {
                const updatedItems = { ...prevItems };
                Object.keys(updatedItems).forEach((key) => {
                    if (updatedItems[key].expiration < now) {
                        delete updatedItems[key];
                    }
                });
                return updatedItems;
            });
        }, 1000);
        return () => clearInterval(interval);
    }, []);

    const handleSetItem = async (e) => {
        e.preventDefault();
        try {
            const response = await axios.post('http://localhost:8000/item', {
                key,
                value,
                expiration: parseInt(expiration, 10)
            });
            setCacheItems((prevItems) => ({
                ...prevItems,
                [key]: { value, expiration: Date.now() + expiration * 1000 },
            }));
            setKey('');
            setValue('');
            setExpiration('');
        } catch (error) {
            console.error('Error setting item:', error);
        }
    };

    const handleGetItem = async (keyToGet) => {
        try {
            const response = await axios.get(`http://localhost:8000/item?key=${keyToGet}`);
            console.log(response.data);
            // Update the state with the fetched item
            setCacheItems((prevItems) => ({
                ...prevItems,
                [keyToGet]: { value: response.data, expiration: Date.now() + 5000 }, // Assuming 5 seconds expiration for demonstration
            }));
        } catch (error) {
            console.error('Error fetching item:', error);
        }
    };

    // Function to delete a specific item from the cache
    const handleDeleteItem = async (keyToDelete) => {
        try {
            await axios.delete(`http://localhost:8000/item?key=${keyToDelete}`);
            setCacheItems((prevItems) => {
                const updatedItems = { ...prevItems };
                delete updatedItems[keyToDelete];
                return updatedItems;
            });
        } catch (error) {
            console.error('Error deleting item:', error);
        }
    };

    return (
        <div>
            <h1>LRU Cache Items</h1>
            <ul>
                {Object.entries(cacheItems).map(([key, item]) => (
                    <li key={key} className="cache-item">
                        {key}: {item.value}
                        <button onClick={() => handleDeleteItem(key)}>Delete</button>
                    </li>
                ))}
            </ul>
            <form onSubmit={handleSetItem} className="form-add-item">
                <input type="text" value={key} onChange={(e) => setKey(e.target.value)} placeholder="Key" required />
                <input type="text" value={value} onChange={(e) => setValue(e.target.value)} placeholder="Value" required />
                <input type="number" value={expiration} onChange={(e) => setExpiration(e.target.value)} placeholder="Expiration (seconds)" required />
                <button type="submit">Set Item</button>
            </form>
        </div>
    );
}

export default App;
